class QuickSort {
    public int[] quickSort(int[] nums) {
        if(nums==null || nums.length<=1) {
            // base conditiion
            return nums;
        }
        quickSort(nums, 0, nums.length-1);
        return nums;
    }                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               

    private void quickSort(int[] nums,int start, int end) {
        if(start>=end) return; // base condition
        // get the pivot position after placing it in correct index
        int pi = partitionByFirstOneAsPivot(nums, start, end); // taking the first element as pivot
        // int pi = partitionByLastOneAsPivot(nums, start, end); // taking the last element as pivot
        // do the partition based on pivot index
        quickSort(nums, start, pi-1);
        quickSort(nums, pi+1, end);
    }

    private int partitionByLastOneAsPivot(int[] nums, int start, int end) {
        int pivot = nums[end];
        int i = start - 1;
        for(int j=start;j<end;j++) {
            if(nums[j]<=pivot) {
                i++;
                swapTheValues(nums, i, j);
            }
        }
        i++; // keep the pivot in after all smaller values 
        swapTheValues(nums, i, end);
        return i;
    }

    private int partitionByFirstOneAsPivot(int[] nums, int start, int end) {
        int pivot = nums[start];
        int i = end + 1;
        for(int j=end;j>start;j--) {
            if(nums[j]>=pivot) {
                i--;
                swapTheValues(nums, i, j);
            }
        }
        i--; // keep the pivot in after all smaller values 
        swapTheValues(nums, i, start);
        return i;
    }

    private void swapTheValues(int[] nums, int i, int j) {
        int value = nums[i];
        nums[i] = nums[j];
        nums[j] = value;
    }
}